<script setup>
definePageMeta({
  layout: 'pos'
})

import { ref, computed } from 'vue'

const searchQuery = ref('')
const category = ref('All')
const categories = ['All', 'Brew Equipment', 'Coffee', 'Espresso', 'Instant + RTD', 'Ceramics', 'Apparel']

const products = [
  { name: 'Fellow Prismo French Black', price: 30, image: 'https://aliyuncdn.antdv.com/logo.png', category: 'Brew Equipment' },
  { name: 'Fellow Stagg Kettle Black', price: 165, image: 'https://aliyuncdn.antdv.com/logo.png', category: 'Brew Equipment' },
  { name: 'Quick Add Baratza Encore Grinder Black', price: 170, image: 'https://aliyuncdn.antdv.com/logo.png', category: 'Brew Equipment' },
  { name: 'Jennings CJ4000 Digital Scale', price: 40, image: 'https://aliyuncdn.antdv.com/logo.png', category: 'Brew Equipment' },
  { name: 'Fellow Prismo French Black', price: 30, image: 'https://aliyuncdn.antdv.com/logo.png', category: 'Brew Equipment' },
  { name: 'Fellow Stagg Kettle Black', price: 165, image: 'https://aliyuncdn.antdv.com/logo.png', category: 'Brew Equipment' },
  { name: 'Quick Add Baratza Encore Grinder Black', price: 170, image: 'https://aliyuncdn.antdv.com/logo.png', category: 'Brew Equipment' },
  { name: 'Jennings CJ4000 Digital Scale', price: 40, image: 'https://aliyuncdn.antdv.com/logo.png', category: 'Brew Equipment' },
]

const orderDetails = ref([
  { name: 'Fellow Clara French Press', price: 198, quantity: 2, image: 'https://aliyuncdn.antdv.com/logo.png' },
  { name: 'Fellow Ode Brew Grinder', price: 299, quantity: 1, image: 'https://aliyuncdn.antdv.com/logo.png' },
  { name: 'Fellow Stagg EKG Kettle Black', price: 165, quantity: 1, image: 'https://aliyuncdn.antdv.com/logo.png' }
])

const subtotal = computed(() => 742)
const discount = computed(() => 4)
const tax = computed(() => 2)
const total = computed(() => subtotal.value - discount.value + tax.value)

const filteredProducts = computed(() => {
  if (category.value === 'All') {
    return products.filter(product =>
        product.name.toLowerCase().includes(searchQuery.value.toLowerCase())
    )
  }
  return products.filter(
      product =>
          product.category === category.value &&
          product.name.toLowerCase().includes(searchQuery.value.toLowerCase())
  )
})
</script>

